package com.vmi.module.model;import com.fasterxml.jackson.annotation.JsonFormat;import com.fasterxml.jackson.annotation.JsonFormat.Shape;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import com.vmi.module.util.AuthorizationToken;import java.util.Date;import java.util.List;import javax.persistence.Transient;import org.springframework.data.annotation.CreatedBy;import org.springframework.data.annotation.CreatedDate;import org.springframework.data.annotation.Id;import org.springframework.data.annotation.LastModifiedBy;import org.springframework.data.annotation.LastModifiedDate;import org.springframework.data.mongodb.core.index.Indexed;import org.springframework.data.mongodb.core.mapping.DBRef;import org.springframework.data.mongodb.core.mapping.Document;import org.springframework.data.mongodb.core.mapping.Field;@Document(collection="USER")@JsonIgnoreProperties({"createDate", "createBy", "modifiedDate", "modifiedBy", "password", "authorizationToken"})public class Users{  @Id  private String id;  @Indexed(unique=true)  private String empName;  @Indexed(unique=true)  private String empCode;  private String empEmail;  private String empPhoneNumber;  @DBRef  @Field("COMPANY_DETAILS")  private CompanyProfile company;  @DBRef  @Field("WAREHOUSE_DETAILS")  private List<WareHouseDetails> WareHouse;  @DBRef  @Field("ROLES")  private Roles role;  private boolean empDataVisible;  private Long lastAccessed;  @JsonFormat(shape=JsonFormat.Shape.STRING, pattern="yyyy-MM-dd HH:MM")  private Date lastLogin;  private String password;  private boolean disabled = false;  private String authorizationToken;  @Transient  private AuthorizationToken authorization;  @CreatedDate  @JsonFormat(shape=JsonFormat.Shape.STRING, pattern="yyyy-MM-dd")  private Date createDate;  @CreatedBy  private String createBy;  @LastModifiedDate  @JsonFormat(shape=JsonFormat.Shape.STRING, pattern="yyyy-MM-dd")  private Date modifiedDate;  @LastModifiedBy  private String modifiedBy;    public String getId()  {    return this.id;  }    public void setId(String id)  {    this.id = id;  }    public String getEmpName()  {    return this.empName;  }    public void setEmpName(String empName)  {    this.empName = empName;  }    public String getEmpCode()  {    return this.empCode;  }    public void setEmpCode(String empCode)  {    this.empCode = empCode;  }    public List<WareHouseDetails> getWareHouse()  {    return this.WareHouse;  }    public void setWareHouse(List<WareHouseDetails> wareHouse)  {    this.WareHouse = wareHouse;  }    public boolean isEmpDataVisible()  {    return this.empDataVisible;  }    public void setEmpDataVisible(boolean empDataVisible)  {    this.empDataVisible = empDataVisible;  }    public Date getCreateDate()  {    return this.createDate;  }    public void setCreateDate(Date createDate)  {    this.createDate = createDate;  }    public String getCreateBy()  {    return this.createBy;  }    public void setCreateBy(String createBy)  {    this.createBy = createBy;  }    public Date getModifiedDate()  {    return this.modifiedDate;  }    public void setModifiedDate(Date modifiedDate)  {    this.modifiedDate = modifiedDate;  }    public String getModifiedBy()  {    return this.modifiedBy;  }    public void setModifiedBy(String modifiedBy)  {    this.modifiedBy = modifiedBy;  }    public String getAuthorizationToken()  {    return this.authorizationToken;  }    public void setAuthorizationToken(String authorizationToken)  {    this.authorizationToken = authorizationToken;  }    public CompanyProfile getCompany()  {    return this.company;  }    public void setCompany(CompanyProfile company)  {    this.company = company;  }    public String getPassword()  {    return this.password;  }    public void setPassword(String password)  {    this.password = password;  }    public Roles getRole()  {    return this.role;  }    public void setRole(Roles role)  {    this.role = role;  }    public Date getLastLogin()  {    return this.lastLogin;  }    public void setLastLogin(Date lastLogin)  {    this.lastLogin = lastLogin;  }    public AuthorizationToken getAuthorization()  {    return this.authorization;  }    public void setAuthorization(AuthorizationToken authorization)  {    this.authorization = authorization;  }    public Long getLastAccessed()  {    return this.lastAccessed;  }    public void setLastAccessed(Long lastAccessed)  {    this.lastAccessed = lastAccessed;  }    public String getEmpEmail()  {    return this.empEmail;  }    public void setEmpEmail(String empEmail)  {    this.empEmail = empEmail;  }    public String getEmpPhoneNumber()  {    return this.empPhoneNumber;  }    public void setEmpPhoneNumber(String empPhoneNumber)  {    this.empPhoneNumber = empPhoneNumber;  }    public boolean isDisabled()  {    return this.disabled;  }    public void setDisabled(boolean disabled)  {    this.disabled = disabled;  }}