package com.vmi.module.model;import com.fasterxml.jackson.annotation.JsonFormat;import com.fasterxml.jackson.annotation.JsonFormat.Shape;import com.fasterxml.jackson.annotation.JsonIgnore;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import java.util.Date;import org.springframework.data.annotation.CreatedBy;import org.springframework.data.annotation.CreatedDate;import org.springframework.data.annotation.Id;import org.springframework.data.annotation.LastModifiedBy;import org.springframework.data.annotation.LastModifiedDate;import org.springframework.data.mongodb.core.mapping.DBRef;import org.springframework.data.mongodb.core.mapping.Document;import org.springframework.data.mongodb.core.mapping.Field;@Document(collection="WAREHOUSE_DETAILS")@JsonIgnoreProperties({"createDate", "createBy", "modifiedDate", "modifiedBy"})public class WareHouseDetails{  @Id  private String id;  private String warehouseName;  private String warehouseCode;  private String warehouseContact;  private String warehouseEmail;  private String warehouseAdress;  private boolean disabled = false;  @JsonIgnore  @DBRef  @Field("COMPANY_DETAILS")  private CompanyProfile companyProfile;  @JsonFormat(shape=JsonFormat.Shape.STRING, pattern="yyyy-MM-dd")  @CreatedDate  private Date createDate;  @CreatedBy  private String createBy;  @JsonFormat(shape=JsonFormat.Shape.STRING, pattern="yyyy-MM-dd")  @LastModifiedDate  private Date modifiedDate;  @LastModifiedBy  private String modifiedBy;    public String getId()  {    return this.id;  }    public void setId(String id)  {    this.id = id;  }    public String getWarehouseName()  {    return this.warehouseName;  }    public void setWarehouseName(String warehouseName)  {    this.warehouseName = warehouseName;  }    public String getWarehouseCode()  {    return this.warehouseCode;  }    public void setWarehouseCode(String warehouseCode)  {    this.warehouseCode = warehouseCode;  }    public String getWarehouseContact()  {    return this.warehouseContact;  }    public void setWarehouseContact(String warehouseContact)  {    this.warehouseContact = warehouseContact;  }    public String getWarehouseEmail()  {    return this.warehouseEmail;  }    public void setWarehouseEmail(String warehouseEmail)  {    this.warehouseEmail = warehouseEmail;  }    public String getWarehouseAdress()  {    return this.warehouseAdress;  }    public void setWarehouseAdress(String warehouseAdress)  {    this.warehouseAdress = warehouseAdress;  }    public Date getCreateDate()  {    return this.createDate;  }    public void setCreateDate(Date createDate)  {    this.createDate = createDate;  }    public String getCreateBy()  {    return this.createBy;  }    public void setCreateBy(String createBy)  {    this.createBy = createBy;  }    public Date getModifiedDate()  {    return this.modifiedDate;  }    public void setModifiedDate(Date modifiedDate)  {    this.modifiedDate = modifiedDate;  }    public String getModifiedBy()  {    return this.modifiedBy;  }    public void setModifiedBy(String modifiedBy)  {    this.modifiedBy = modifiedBy;  }    public CompanyProfile getCompanyProfile()  {    return this.companyProfile;  }    public void setCompanyProfile(CompanyProfile companyProfile)  {    this.companyProfile = companyProfile;  }    public boolean isDisabled()  {    return this.disabled;  }    public void setDisabled(boolean disabled)  {    this.disabled = disabled;  }}